glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform ARGS {
  vec2 u_size;
  float u_time;
  float u_rate;
};
vec2 wave(in vec2 p) {
    float d = u_time*u_rate, x=8.*(p.x+d), y=8.*(p.y+d);
    return vec2(cos(x-y)*cos(y), sin(x+y)*sin(y));
}
void main () {
  vec4 o = vec4(1, 1, 1, 1);
  o *= v_color;
  ALPHA_TEST(o);
  vec2 q = v_uv0 + 2.0/u_size.x * (wave(v_uv0) - wave(v_uv0+u_size));
  gl_FragColor = o * texture(texture, q);
}

